import React, { useState, useReducer } from "react";
import { getNativeByChain, getExplorer } from "../../helpers/networks"
import {
    useMoralis,
    useMoralisQuery,
    useWeb3ExecuteFunction
} from "react-moralis";
import {
    Divider, Card, Image,
    Tooltip, Modal, Badge, Alert, Spin
} from "antd";
import {
    FileSearchOutlined,
    ShoppingCartOutlined,
} from "@ant-design/icons";
import { useVerifyMetadata } from "hooks/useVerifyMetadata";
import { useNFTTokenIds } from "hooks/useNFTTokenIds";
import { mainMarketAddress, deployedABI, createdMarketItemsTable } from "../../MarketplaceSCMetadata";
import {
    NFTCardStyle,
    NFTsDiv,
    NFTImg,
    brightFontCol,
    NFTImgWrapperStyle,
    pageTitleMidStyle,
    descriptionStyle
} from "../../GlobalStyles";
import { AllowedNftContracts } from "../../MglNftMetadata";
import { MainChainID } from "../../MglNftMetadata";
import { chainIdToNameAndLogo } from "../Chains/Chains";
import Loader from "../Loader";


const styles = {
    banner: {
        display: "flex",
        alignItems: "center",
    },
    logo: {
        height: "100px",
        width: "100px",
        borderRadius: "50%",
        border: "solid 4px white",
        margin: "0 1rem",
    },
};

const { Meta } = Card;
const fallbackImg =
    "data:image/png;base64,";

function NFTCollectionItems({ nftAddress, colName, colImg }) {

    const { chainId, isAuthenticated, account, Moralis } = useMoralis();
    const userChainId = chainId;
    const marketPlaceChainId = MainChainID;
    const { data: NFTTokenIds, error: NFTsFetchError, isLoading } = useNFTTokenIds(nftAddress, 3, marketPlaceChainId);

    const [visible, setVisibility] = useState(false);
    const [nftToBuy, setNftToBuy] = useState(null);
    const [loading, setLoading] = useState(false);

    const contractABI = deployedABI;
    const marketAddress = mainMarketAddress;
    const chainName = chainIdToNameAndLogo.get(marketPlaceChainId)[0];
    const chainLogo = chainIdToNameAndLogo.get(marketPlaceChainId)[1];

    const contractProcessor = useWeb3ExecuteFunction();
    const nativeName = getNativeByChain(marketPlaceChainId);
    const contractABIJson = JSON.parse(contractABI);
    const { verifyMetadata } = useVerifyMetadata();
    const listings = new Map();

    // eslint-disable-next-line no-unused-vars
    const [ignored, forceUpdate] = useReducer(x => x + 1, 0);

    const queryMarketItems = useMoralisQuery(
        createdMarketItemsTable, query => {
            // get not sold items
            return query
                .equalTo("sold", false)
                .equalTo("confirmed", true)
                .containedIn("nftContract",
                    AllowedNftContracts.get(chainId)?.map(c => c.toLowerCase())
                );
        });
    const fetchMarketItems = JSON.parse(
        JSON.stringify(queryMarketItems.data, [
            "objectId",
            "createdAt",
            "price",
            "nftContract",
            "itemId",
            "sold",
            "tokenId",
            "seller",
            "owner",
            "confirmed",
        ])
    );
    const purchaseItemFunction = "createMarketSale";

    async function purchase() {
        setLoading(true);
        const tokenDetails = getMarketWithLowestPrice(nftToBuy);
        const itemID = tokenDetails.itemId;
        const tokenPrice = tokenDetails.price;
        const ops = {
            contractAddress: marketAddress,
            functionName: purchaseItemFunction,
            abi: contractABIJson,
            params: {
                nftContract: nftToBuy.token_address,
                itemId: itemID,
            },
            msgValue: tokenPrice,
        };

        await contractProcessor.fetch({
            params: ops,
            onSuccess: async () => {
                setLoading(false);
                setVisibility(false);
                await updateSoldMarketItem(tokenDetails);
                forceUpdate();
                succPurchase();
            },
            onError: (error) => {
                setLoading(false);
                failPurchase(error);
            },
        });
    }

    const handleBuyClick = (nft) => {
        if (!isAuthenticated) {
            alert(`
            You need to connect your wallet\n
            to be able to buy NFTs
            `);
            return;
        } else if (userChainId !== MainChainID) {
            alert(`
            Please switch to\n
            Avalanche Fuji Testnet Network\n
            to be able to buy NFTs
            `);
            return;
        }
        setNftToBuy(nft);
        setVisibility(true);
    };

    function succPurchase() {
        let secondsToGo = 10;
        const modal = Modal.success({
            title: "Success!",
            content: (
                <>
                    <p>You have purchased this NFT</p>
                    <p>It may take around <b>5 minutes</b></p>
                    <p>until it will appear at (your NFTs) section</p>
                </>
            ),
        });
        setTimeout(() => {
            modal.destroy();
        }, secondsToGo * 1000);
    }

    function failPurchase(err) {
        // const modal = 
        Modal.error({
            title: "Error!",
            content: `There was a problem when purchasing this NFT\n
            Make sure to connect your wallet,\n
            and choose correct blockchain`,
        });
        // let secondsToGo = 5;
        // setTimeout(() => {
        //   modal.destroy();
        // }, secondsToGo * 1000);
    }

    async function updateSoldMarketItem(tokenDetails) {
        const id = tokenDetails.objectId;
        const marketList = Moralis.Object.extend(createdMarketItemsTable);
        const query = new Moralis.Query(marketList);
        await query.get(id).then((obj) => {
            obj.set("sold", true);
            obj.set("owner", account);
            obj.save();
        });
    }

    // TODO workaround for now
    // MGL nft drops will have same price
    const getMarketWithLowestPrice = (nft) => {
        const items = getMarketItems(nft);
        if (items.length === 1) return items[0];
        if (items.length > 1) {
            return items.sort((a, b) => a.price - b.price)[0]
        }
        return items;
    };

    const getMarketItems = (nft) => {
        const result = fetchMarketItems.filter(
            (e) =>
                e.nftContract === nft?.token_address &&
                e.tokenId === nft?.token_id
        );
        const key = `${nft?.token_address}:${nft?.token_id}`
        listings.set(key, result.length);
        return result;
    };

    const hasMarketItems = (nft) => {
        const result = getMarketItems(nft);
        return result.length > 0;
    };

    const getAmountForSale = (nft) => {
        const key = `${nft?.token_address}:${nft?.token_id}`
        return listings.get(key)
    }

    if (isLoading) {
        return (<Loader />);
    } else {

        return (
            <>
                {/* Smart Contract Alert */}
                {contractABIJson.noContractDeployed && (
                    <>
                        <br /><br />
                        <Alert
                            message="No Smart Contract Details Provided. Please deploy smart contract and provide address + ABI in the MoralisDappProvider.js file"
                            type="error"
                        />
                        <div style={{ marginBottom: "10px" }}></div>
                    </>
                )}
                {(
                    <>
                        {NFTsFetchError && (
                            <>
                                <Alert
                                    message="Unable to fetch all NFT metadata... We are searching for a solution, please try again later!"
                                    type="warning"
                                />
                                <div style={{ marginBottom: "10px" }}></div>
                            </>
                        )}
                    </>
                )}
                {/* NFT token description */}
                <div style={{
                    ...styles.banner,
                    ...pageTitleMidStyle,
                }}>
                    <Image
                        preview={false}
                        src={colImg}
                        fallback={fallbackImg}
                        alt=""
                        style={styles.logo}
                    />
                    <div>
                        {colName}
                    </div>
                </div>
                <Divider style={{ backgroundColor: brightFontCol }} />
                <div style={NFTsDiv}>
                    {NFTTokenIds && shuffle(NFTTokenIds.result)
                        .map((nft, index) => {
                            //Verify Metadata
                            nft = verifyMetadata(nft);
                            return (
                                <Card
                                    key={index}
                                    hoverable
                                    actions={[
                                        <Tooltip title="View On Blockexplorer">
                                            <FileSearchOutlined
                                                onClick={() =>
                                                    window.open(
                                                        `${getExplorer(marketPlaceChainId)}address/${nft.token_address}`,
                                                        "_blank"
                                                    )
                                                }
                                            />
                                        </Tooltip>,
                                        <Tooltip title="Buy NFT">
                                            <ShoppingCartOutlined onClick={() => handleBuyClick(nft)} />
                                        </Tooltip>,
                                    ]}
                                    style={NFTCardStyle}
                                    cover={
                                        <Image
                                            preview={false}
                                            src={nft.image || "error"}
                                            fallback={fallbackImg}
                                            alt=""
                                            style={NFTImg}
                                            wrapperStyle={{
                                                backgroundColor: "#" + nft?.background_color,
                                                ...NFTImgWrapperStyle
                                            }}
                                        />
                                    }
                                >
                                    {hasMarketItems(nft) ? (
                                        <div onClick={() => handleBuyClick(nft)}>
                                            <Badge.Ribbon
                                                text="Buy Now"
                                                color="green"
                                                style={{
                                                    paddingRight: "5px",
                                                    paddingLeft: "5px",
                                                    marginRight: "-10px",
                                                }}
                                            />
                                        </div>
                                    ) : (
                                        <Badge.Ribbon
                                            text="Sold Out"
                                            color="orange"
                                            style={{
                                                paddingRight: "5px",
                                                paddingLeft: "5px",
                                                marginRight: "-10px",
                                            }}
                                        />
                                    )}
                                    <Meta
                                        title={nft.name}
                                        description={
                                            <>
                                                <p>{`#${nft.token_id}`}</p>
                                                <div style={{
                                                    textAlign: "center"
                                                }}>
                                                    <h3>
                                                        <b style={{ color: "darkblue" }}>Total</b>
                                                        &nbsp;<b style={{ color: "cadetblue" }}>/</b>&nbsp;
                                                        <b style={{ color: "crimson" }}>For Sale</b>
                                                    </h3>
                                                    <h1>
                                                        <b style={{ color: "darkblue" }}>{nft.amount}</b>
                                                        &nbsp;<b style={{ color: "cadetblue" }}>/</b>&nbsp;
                                                        <b style={{ color: "crimson" }}>{getAmountForSale(nft)}</b>
                                                    </h1>
                                                    <div>
                                                        {hasMarketItems(nft) ? (
                                                            <h4 style={{
                                                                backgroundColor: "burlywood",
                                                                borderRadius: "1rem",
                                                                padding: "2px",
                                                            }}>
                                                                Best Price: <b style={{ color: "darkblue" }}>
                                                                    {
                                                                        getMarketWithLowestPrice(nft).price / ("1e" + 18)
                                                                    }
                                                                </b>
                                                                &nbsp;
                                                                <b style={{ color: "darkblue" }}>{nativeName}</b>
                                                            </h4>
                                                        ) : (
                                                            <>
                                                                <br />
                                                            </>
                                                        )}
                                                    </div>
                                                </div>
                                            </>
                                        }
                                    />
                                </Card>
                            )
                        })}
                    <div style={{
                        flexBasis: "100%",
                    }}></div>
                    <div style={{
                        display: "flex",
                        alignItems: "center",
                        ...descriptionStyle,
                    }}>
                        NFTs collection on&nbsp;
                        <span style={{
                        }}>{chainName}</span>
                        &nbsp;
                        {chainLogo}
                    </div>
                    {/* TODO get the one with lowest price */}
                    {/* modal boxes to be able to buy */}
                    {hasMarketItems(nftToBuy) ? (
                        <Modal
                            title={
                                <>
                                    Buy <b>{nftToBuy?.name}</b> #{nftToBuy?.token_id}
                                </>
                            }
                            visible={visible}
                            onCancel={() => setVisibility(false)}
                            onOk={() => purchase()}
                            okText="Buy"
                        >
                            <Spin spinning={loading}>
                                <div
                                    style={{
                                        width: "250px",
                                        margin: "auto",
                                    }}
                                >
                                    <Badge.Ribbon
                                        color="green"
                                        text={`${getMarketWithLowestPrice(nftToBuy).price / ("1e" + 18)
                                            } ${nativeName}`}
                                        style={{
                                            marginRight: "-100px",
                                        }}
                                    >
                                    </Badge.Ribbon>
                                    <img
                                        src={nftToBuy?.image}
                                        alt=""
                                        style={{
                                            width: "250px",
                                            borderRadius: "10px",
                                            marginBottom: "15px",
                                        }}
                                    />
                                </div>
                            </Spin>
                        </Modal>
                    ) : (
                        <Modal
                            title={
                                <>
                                    Buy <b>{nftToBuy?.name}</b> #{nftToBuy?.token_id}
                                </>
                            }
                            visible={visible}
                            onCancel={() => setVisibility(false)}
                            onOk={() => setVisibility(false)}
                        >
                            <img
                                src={nftToBuy?.image}
                                alt=""
                                style={{
                                    width: "250px",
                                    margin: "auto",
                                    borderRadius: "10px",
                                    marginBottom: "15px",
                                }}
                            />
                            <Alert
                                message="This NFT is currently not for sale"
                                type="warning"
                            />
                        </Modal>
                    )}
                </div>
            </>
        );
    }
}

export default NFTCollectionItems;

const shuffle = (arr) => {
    return arr.map(value => ({ value, sort: Math.random() }))
        .sort((a, b) => a.sort - b.sort)
        .map(({ value }) => value)
}
